mapper.update:
update bg_blog
title = #{title}, summary = #{summary}, category_id = #{categoryId}, status = #{status}, weight = #{weight}, support = #{support}, header_img = #{headerImg}, content = #{content}, type = #{type}, update_by = #{updateBy}, update_time = sysdate()
where blog_id = #{blogId}

mapper.select:
select blog_id, title, status, weight, support, click, type,
create_time, update_time
from bg_blog

    
        and title like concat('%',#{title},'%')
    
    
        AND status = #{status}
    
    
        AND support = #{support}
    
    
        AND type = #{type}
    
    
        AND category_id = #{categoryId}
    
    
        AND date_format(create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
    
    
        AND date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
